mapper.select:
select
  user_id, name, id_num, phone_num, heart_rate, temperature, respiratory_rate, step_
 limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from dirver_info


        and user_id = #{userId}


        and name = #{name}


        and id_num = #{idNum}


        and phone_num = #{phoneNum}


        and heart_rate = #{heartRate}


        and temperature = #{temperature}


        and respiratory_rate = #{respiratoryRate}


        and step_number = #{stepNumber}


        and blood_oxygen = #{bloodOxygen}


        and stress = #{stress}


        and emo = #{emo}


        and istired = #{istired}


        and collection_date = #{collectionDate}


        and reserve = #{reserve}


        and reserve2 = #{reserve2}


        and reserve3 = #{reserve3}


        and reserve4 = #{reserve4}


        and reserve5 = #{reserve5}


        and reserve6 = #{reserve6}
