mapper.select:
select
  equipment_id, equipment_state, bind_user_id, bind_username, last_communicate_date
from equipment


        and equipment_id = #{equipmentId}


        and equipment_state = #{equipmentState}


        and bind_user_id = #{bindUserId}


        and bind_username = #{bindUsername}


        and last_communicate_date = #{lastCommunicateDate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from equipment


        and equipment_id = #{equipmentId}


        and equipment_state = #{equipmentState}


        and bind_user_id = #{bindUserId}


        and bind_username = #{bindUsername}


        and last_communicate_date = #{lastCommunicateDate}
