mapper.select:
select
role_id, role_name, description, created_at, updated_at
from roles


        and role_id = #{roleId}


        and role_name = #{roleName}


        and description = #{description}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from roles


        and role_id = #{roleId}


        and role_name = #{roleName}


        and description = #{description}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
