mapper.select:
select
user_role_id, user_id, role_id, assigned_at
from userroles


        and user_role_id = #{userRoleId}


        and user_id = #{userId}


        and role_id = #{roleId}


        and assigned_at = #{assignedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from userroles


        and user_role_id = #{userRoleId}


        and user_id = #{userId}


        and role_id = #{roleId}


        and assigned_at = #{assignedAt}
