mapper.select:
select
user_id, username, password, email, created_at, updated_at
from users


        and user_id = #{userId}


        and username = #{username}


        and password = #{password}


        and email = #{email}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from users


        and user_id = #{userId}


        and username = #{username}


        and password = #{password}


        and email = #{email}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
